import React from 'react';

import { LinearGradient } from "expo-linear-gradient";
import { View, StyleSheet, Text, TextInput, Image, TouchableHighlight } from 'react-native';

import { SafeAreaView } from 'react-native-safe-area-context';
class LoginScreen extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
    render() {
        return (
            <LinearGradient
                colors={["#2479AD", "#16ADA2", "#01FF90"]}
                start={[0.7, 0.3]}
                style={styles.gradientContainer}
            >
                <SafeAreaView style={styles.internalContainer}>
                    <View style={styles.pageContainer}>
                        <View style={styles.headingContainer}>
                            <Text style={styles.headingText}>
                                Sign In
                        </Text>

                        </View>
                        <View style={styles.formContainer}>
                            <View style={styles.flexInput}>
                                <TextInput
                                    style={styles.inputText}
                                    placeholder="Username"
                                    value={this.state.username}
                                    autoCapitalize='none'
                                    autoCorrect={false}
                                    onChangeText={(username) => { this.setState({ username }) }}
                                    placeholderTextColor="#78849E"
                                />

                            </View>
                            <View style={styles.flexInput}>
                                <TextInput
                                    style={styles.inputText}
                                    placeholder="Password"
                                    placeholderTextColor="#78849E"
                                    autoCapitalize='none'
                                    autoCorrect={false}
                                    value={this.state.password}
                                    onChangeText={(password) => { this.setState({ password }) }}
                                    secureTextEntry
                                />

                            </View>
                        </View>
                    </View>
                </SafeAreaView>
            </LinearGradient>
        );
    }
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
        alignItems: "center",
        justifyContent: "center",
    },
    gradientContainer: {
        width: '100%',
        height: '100%'
    },
    headingText: {
        fontSize: 30,
        color: '#fff',
        fontWeight: '600',
        lineHeight: 45
    },
    headingContainer: {
        width: '100%',
        height: '30%',
        marginTop: 10,
        justifyContent: "center",
        padding: '10%'
    },
    narrativeContainer: {
        width: '100%',
        height: '20%',
        marginTop: 10,
        justifyContent: "center",
        padding: 30
    },
    narrativeText: {
        fontSize: 20,
        color: '#fff',
        fontWeight: 'bold',
        lineHeight: 30
    },
    inputText: {
        marginTop:15,
        backgroundColor: '#fff',
        borderRadius: 10,
        elevation: 5,
        shadowColor: '#78849E',
        shadowOpacity: 0.2,
        shadowOffset: {
            width: 1,
            height: 2
        },
        width: 300,
        padding: 14,

    },
    buttonContainer: {
        padding: 30,
        justifyContent: "center",
        flexDirection: 'row',
        width: '100%',
        alignItems: "center"
    },
    buttonStyle: {
        backgroundColor: '#2479AD',
        margin: 10,
        paddingTop: 14,
        paddingBottom: 14,
        width: "50%",
        alignItems: "center",
        borderRadius: 14
    },
    buttonText: {
        color: '#fff'
    },
    flexInput: {
        alignItems: "center",
        flexDirection: 'row',

    },
    pageContainer: {
        // flex:1,
        width: '100%',
        height: '100%',
        // flexDirection: 'column',
        alignItems: "center"
    }
});

export default LoginScreen;
